import React from "react";
import {
  ImageBackground,
  SafeAreaView,
  View,
  Text,
  StyleSheet,
  FlatList,
  Image,
  Dimensions,
  ScrollView,
  TouchableOpacity,
} from "react-native";

import Icon from "react-native-vector-icons/MaterialIcons";
// import COLORS from '../../consts/colors';
import colors from "../../../src/consts/colors";

const { width } = Dimensions.get("screen");
const DetailsScreen = ({ navigation, route }) => {
  const house = route.params;

  const InteriorCard = ({ interior }) => {
    return <Image source={interior} style={style.interiorImage} />;
  };

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: colors.white }}>
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* House image */}

        <View style={style.backgroundImageContainer}>
          <ImageBackground style={style.backgroundImage} source={house.image}>
            <View style={style.header}>
              <View style={style.headerBtn}>
                <Icon
                  name="arrow-back-ios"
                  size={20}
                  onPress={navigation.goBack}
                />
              </View>
            </View>
          </ImageBackground>
        </View>

        <View style={style.detailsContainer}>
          {/* Name and rating view container */}
          <View
            style={{ flexDirection: "row", justifyContent: "space-between" }}
          >
            <Text style={{ fontSize: 20, fontWeight: "bold" }}>Rome suite</Text>
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <View style={style.ratingTag}>
                <Text style={{ color: colors.white }}>4.8</Text>
              </View>
              <Text style={{ fontSize: 13, marginLeft: 5 }}></Text>
            </View>
          </View>

          {/* Location text */}
          <Text style={{ fontSize: 16, color: colors.grey }}>
            Polokwane Central
          </Text>

          {/* icon container */}
          <View style={{ flexDirection: "row", marginTop: 20 }}>
            <View style={style.facility}>
              <Icon name="hotel" size={18} />
              <Text style={style.facilityText}>2</Text>
            </View>
            <View style={style.facility}>
              <Icon name="bathtub" size={18} />
              <Text style={style.facilityText}>3</Text>
            </View>
            <View style={style.facility}>
              <Icon name="aspect-ratio" size={18} />
              <Text style={style.facilityText}>100m area</Text>
            </View>
          </View>
          <Text style={{ marginTop: 20, color: colors.grey }}>
            Rome Suites has an outdoor pool and terrace, and guests can enjoy a
            meal at the restaurant.
          </Text>

          {/* <Text style={{marginTop: 20, color: colors.grey}}>
            {house.details}
          </Text> */}

          {/* Interior list */}
          <FlatList
            contentContainerStyle={{ marginTop: 20 }}
            horizontal
            showsHorizontalScrollIndicator={false}
            keyExtractor={(_, key) => key.toString()}
            data={house.interiors}
            renderItem={({ item }) => <InteriorCard interior={item} />}
          />

          {/* footer container */}
          <View style={style.footer}>
            <View>
              <Text
                style={{
                  color: colors.purple,
                  fontWeight: "bold",
                  fontSize: 18,
                }}
              >
                R4,500
              </Text>
              <Text
                style={{ fontSize: 12, color: colors.grey, fontWeight: "bold" }}
              >
                Total Price
              </Text>
            </View>
            <View style={style.bookNowBtn}>
              {/* <View>
                   <DatePicker
                    selected = {selectedDate}
                    onChange={date => setSelectedDate(Date)}
                    dateFormat ='dd/MM/yyyy'
                    // minDate={new Date()}
                    filterDate = {Date => date.getDay() = 6 && date.get}
                    />
                 </View> */}
              <TouchableOpacity
                onPress={() => navigation.navigate("CheckOut")}
                activeOpacity={0.7}>
                <Text style={{ color: colors.white }}>Reserve</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const style = StyleSheet.create({
  backgroundImageContainer: {
    elevation: 20,
    marginHorizontal: 20,
    marginTop: 15,
    alignItems: "center",
    height: 300,
  },
  backgroundImage: {
    height: "100%",
    width: "100%",
    borderRadius: 20,
    overflow: "hidden",
  },
  header: {
    paddingVertical: 20,
    flexDirection: "row",
    justifyContent: "space-between",
    paddingHorizontal: 10,
  },
  headerBtn: {
    height: 50,
    width: 50,
    backgroundColor: colors.white,
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  ratingTag: {
    height: 30,
    width: 35,
    backgroundColor: colors.purple,
    borderRadius: 5,
    justifyContent: "center",
    alignItems: "center",
  },

  interiorImage: {
    width: width / 3 - 20,
    height: 80,
    marginRight: 10,
    borderRadius: 10,
  },
  footer: {
    height: 70,
    backgroundColor: colors.light,
    borderRadius: 10,
    paddingHorizontal: 20,
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "space-between",
    marginVertical: 10,
  },
  bookNowBtn: {
    height: 50,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: colors.purple,
    borderRadius: 10,
    paddingHorizontal: 10,
    width: 110,
    height: 37,
  },
  detailsContainer: { flex: 1, paddingHorizontal: 20, marginTop: 40 },
  facility: { flexDirection: "row", marginRight: 15 },
  facilityText: { marginLeft: 5, color: colors.grey },
});

export default DetailsScreen;
